
public class NumberCalc {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//사칙연산을 콘솔에 표시하는 코드를 작성한것
		System.out.println(50+30);
		System.out.println(50-30);
		System.out.println(50*30);
		System.out.println(50/30);
		System.out.println(50%30);
		/*+는 더하기
		 *-는  뺄셈
		 **는 곱셈
		 *%는 나누기의 나머지
		 */

	}   

}
